sp_widget.script:
(function($sp, input, data, options, gs) {
    data.widgetform = '9f51e3ebdbb12010f46dcd3f29961908' // form widget; '85357f52cb30020000f8d856634c9c24'; // ticket
    var isPopup = false;
    if (input) {
        data.view = input.view;
        if (input.isPopup === true)
            isPopup = true;
    } else {
        data.view = options.view || $sp.getParameter("view") || $sp.getParameter("v"); // no default
    }
    data.query = $sp.getParameter("query") || options.query || "";
    data.responses = [];
    data.table = options.table;
    var gr = new GlideRecord(data.table);
    gr.query();
    while (gr.next()) {
        var rec = $sp.getRecord(data.table, gr.sys_id);
        var f = $sp.getForm(data.table, gr.sys_id, data.query, data.view, isPopup);
        data.responses.push(f);
    }
})($sp, input, data, options, gs);
